mapper.insert:
insert into order_detail
    (
         name, image, order_id, dish_id, setmeal_id, dish_flavor, number, amount
    )
values

        (
            #{orderDetail.name}, #{orderDetail.image}, #{orderDetail.orderId}, #{orderDetail.dishId},
            #{orderDetail.setmealId}, #{orderDetail.dishFlavor}, #{orderDetail.number}, #{orderDetail.amount}
        )

mapper.select:
select * from orders

        and user_id = #{userId}

order by order_time desc

select * from orders

        and number like concat('%', #{number}, '%')
        and phone like concat('%', #{phone}, '%')
        and status = #{status}
        and order_time >= #{beginTime}
        and order_time <= #{endTime}

    order by order_time desc

mapper.update:
update orders
status = #{status}, pay_status = #{payStatus}, delivery_status = #{deliveryStatus}, delivery_time = #{deliveryTime}, cancel_reason = #{cancelReason}, rejection_reason = #{rejectionReason}, cancel_time = #{cancelTime}, estimated_delivery_time = #{estimatedDeliveryTime}, tableware_status = #{tablewareStatus}, tableware_number = #{tablewareNumber}, pack_amount = #{packAmount}, amount = #{amount}, pay_method = #{payMethod}, checkout_time = #{checkoutTime}, order_time = #{orderTime}, address_book_id = #{addressBookId}, user_id = #{userId}, number = #{number}, remark = #{remark}, phone = #{phone}, address = #{address}, consignee = #{consignee},
where id = #{id}
